function Question({ question }) {
  return (
    <div>
      <h4>{question.questions}</h4>

      <div className="options">
        {question.options.map((option) => (
          <button className="btn btn-option" key={option}>
            {option}
          </button>
        ))}
      </div>
    </div>
  );
}

export default Question;
